!function(){"use strict";class e{constructor(){this.acceptedEl=document.querySelector(".accepted"),this.acceptedHiddenEl=document.querySelector("#accepted-hidden"),this.acceptedCloseEl=document.querySelector(".accepted__close")}init(){this.addAcceptedHiddenElClickListener(),this.addAcceptedCloseElClickListener(),this.addAcceptedElClickListener()}addAcceptedHiddenElClickListener(){this.acceptedHiddenEl.addEventListener("click",()=>{this.acceptedEl.classList.add("active")})}addAcceptedCloseElClickListener(){this.acceptedCloseEl.addEventListener("click",()=>{this.acceptedEl.classList.remove("active")})}addAcceptedElClickListener(){this.acceptedEl.addEventListener("click",e=>{e.target==this.acceptedEl&&this.acceptedEl.classList.remove("active")})}}window.addEventListener("load",()=>{(new e).init()})}();